(function(global){
  function SimpleChart(ctx, cfg){
    this.ctx = ctx.getContext('2d');
    this.type = cfg.type || 'line';
    this.data = cfg.data || {labels:[],datasets:[]};
    this.draw();
  }
  SimpleChart.prototype.draw = function(){
    var ctx = this.ctx; var w = ctx.canvas.width; var h = ctx.canvas.height;
    ctx.clearRect(0,0,w,h);
    ctx.fillStyle = '#0b1220'; ctx.fillRect(0,0,w,h);
    ctx.strokeStyle = '#93c5fd'; ctx.lineWidth = 2;
    var ds = this.data.datasets[0] || {data:[]};
    var arr = ds.data || []; var max = Math.max(1, Math.max.apply(null, arr));
    var step = (w-40)/Math.max(1, arr.length-1);
    ctx.beginPath();
    for (var i=0;i<arr.length;i++){
      var x = 20 + i*step;
      var y = h-20 - (arr[i]/max)*(h-60);
      if (i===0) ctx.moveTo(x,y); else ctx.lineTo(x,y);
    }
    ctx.stroke();
  }
  global.Chart = function(ctx, cfg){ return new SimpleChart(ctx, cfg); }
})(this);
